                                        <th><b>Order Qty</b></th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.ListOrders.map((prop) => {
                                        return <tr>
                                            <td>{prop["orderId"]}</td>
                                            <td>{prop["orderNumber"]}</td>
                                            {/* <td>Items lineItemKey</td> */}
                                            <td>{prop["orderStatus"]}</td>
                                            <td>{prop["orderDate"]}</td>
                                            {/* <td>Items Sku</td>
                                            <td>Items Name</td>
                                            <td>Items Qty</td>
                                            <td>Size</td>
                                            <td></td> */}
                                            <td>{prop["items"].length}</td>
                                        </tr>
                                    })}
                                </tbody>
                            </Table>
                        </Row>
                    </Card.Body>
                </Card>
            </Container>
        )
    }
}

export default ListOrders;
